mapper.select:
select lt.crowd_date,
bc.name crowdName,
group_concat(bct.name) crowdType,
group_concat(masculine) masculine,
group_concat(detection) detection,
group_concat(sampling) sampling,
sum(masculine) totalMasculine,
sum(detection) totalDetection,
sum(sampling) totalSampling
from (
select
crowd_type_id,
crowd_id,
crowd_date,
SUM(masculine_total) masculine,
SUM(detection_total) detection,
SUM(sampling_total) sampling
from bs_crowd_total where flag = 1


         AND crowd_date >= #{sampling_date_start} 


         AND crowd_date <= #{sampling_date_end} 


        AND province_id = #{province_id}


        AND city_id = #{city_id}


        AND county_id = #{county_id}


        AND province_id = #{areaProvince}


        AND city_id = #{areaCity}


        AND county_id = #{areaCounty}


group by crowd_type_id, crowd_id,crowd_date) lt
left join bs_crowd bc on bc.kid = lt.crowd_id
left join bs_crowd_type bct on bct.kid = lt.crowd_type_id group by crowdName,crowd_date ORDER BY lt.crowd_date

select crowd_date, group_concat(crowdName SEPARATOR '|') crowdName, group_concat(crowdType SEPARATOR '|') crowdType, group_concat(masculine SEPARATOR '|') masculine, group_concat(detection SEPARATOR '|') detection, group_concat(sampling SEPARATOR '|') sampling, group_concat(totalMasculine SEPARATOR '|') totalMasculine, group_concat(totalDetection SEPARATOR '|') totalDetection, group_concat(totalSampling SEPARATOR '|') totalSampling from (select lt.crowd_date,bc.name crowdName, group_concat(bct.name) crowdType, group_concat(masculine) masculine, group_concat(detection) detection, group_concat(sampling) sampling, sum(masculine) totalMasculine, sum(detection) totalDetection, sum(sampling) totalSampling from ( select crowd_type_id, crowd_id, crowd_date, SUM(masculine_total) masculine, SUM(detection_total) detection, SUM(sampling_total) sampling from bs_crowd_total where flag = 1
AND crowd_date >= #{sampling_date_start}
AND crowd_date <= #{sampling_date_end}
AND province_id = #{province_id}
AND city_id = #{city_id}
AND county_id = #{county_id}
AND province_id = #{areaProvince}
AND city_id = #{areaCity}
AND county_id = #{areaCounty}
group by crowd_type_id, crowd_id,crowd_date) lt left join bs_crowd bc on bc.kid = lt.crowd_id left join bs_crowd_type bct on bct.kid = lt.crowd_type_id group by crowdName,crowd_date) lt group by crowd_date ORDER BY crowd_date
