mapper.select:
select masculine,detection,sampling,crowdName,crowdType from (select lt.masculine,lt.detection,lt.sampling,bc.name crowdName,bct.name crowdType
from (select crowd_type_id,crowd_id,
   SUM(masculine_total) masculine,
   SUM(detection_total) detection,
   SUM(sampling_total) sampling
from bs_crowd_total
where crowd_date = #{crowd_date}


        AND province_id = #{province_id}


        AND city_id = #{city_id}


        AND county_id = #{county_id}


group by crowd_type_id,crowd_id) lt
left join bs_crowd bc on bc.kid = lt.crowd_id
left join bs_crowd_type bct on bct.kid = lt.crowd_type_id)t order by crowdType asc

select crowdName, group_concat(crowdType)crowdType, group_concat(masculine)masculine, group_concat(detection)detection, group_concat(sampling)sampling from ( select crowdName,crowdType,masculine,detection,sampling from (select lt.masculine,lt.detection,lt.sampling,bc.name crowdName,bct.name crowdType from (select crowd_type_id,crowd_id, SUM(masculine_total) masculine, SUM(detection_total) detection, SUM(sampling_total) sampling from bs_crowd_total where crowd_date = #{crowd_date}
AND province_id = #{province_id}
AND city_id = #{city_id}
AND county_id = #{county_id}
group by crowd_type_id,crowd_id) lt left join bs_crowd bc on bc.kid = lt.crowd_id left join bs_crowd_type bct on bct.kid = lt.crowd_type_id)t order by crowdType ASC) gt group by crowdName

select name,GROUP_CONCAT(profession) profession,GROUP_CONCAT(profession_total) profession_total from ( select sa.name,sd.name profession,lt.profession_total from ( select city_id,profession,count(profession) profession_total from bs_employee
AND sampling_date >= #{sampling_date_start}
AND sampling_date <= #{sampling_date_end}
group by profession,city_id ORDER BY profession ASC) lt left join sys_area sa on sa.kid = lt.city_id left join sys_dict sd on sd.KID = lt.profession) gt group by name

select area,GROUP_CONCAT(site_type) site_type,GROUP_CONCAT(type_total) type_total from (select sd.NAME site_type,lt.type_total,sa.name area from ( select site_type,city_id,count(site_type) type_total from bs_environment
AND sampling_date >= #{sampling_date_start}
AND sampling_date <= #{sampling_date_end}
group by site_type,city_id ORDER BY site_type ASC) lt left join sys_dict sd on sd.KID = lt.site_type left join sys_area sa on sa.KID = lt.city_id) gt group by area

SELECT area_name,GROUP_CONCAT(sample_total) sample_total,GROUP_CONCAT(sample_type) sample_type FROM (select area_name,sample_type,SUM(sample_total) sample_total from ( select sa.name area_name,sample_type,sample_total from ( select CASE WHEN sample_type = '00000000606324b5ffffffffda634a10' THEN '产品外包装样本' ELSE '其余外环境样本' END sample_type,city_id,count(sample_type) sample_total from bs_environment
AND sampling_date >= #{sampling_date_start}
AND sampling_date <= #{sampling_date_end}
group by sample_type,city_id ORDER BY sample_type ASC) oblt left join sys_area sa on sa.KID = oblt.city_id) lt GROUP BY area_name,sample_type) gblt GROUP BY area_name

select group_concat(sd.NAME) category,sum(1) total from
(select sample_type from bs_environment


         AND sampling_date >= #{sampling_date_start} 


         AND sampling_date <= #{sampling_date_end} 


group by sample_type ORDER BY sample_type ASC) lt
left join sys_dict sd on sd.KID = lt.sample_type

select name,GROUP_CONCAT(entrance,',','中高风险地区') entrance,GROUP_CONCAT(total,',',risk) total from (select name,GROUP_CONCAT(entrance) entrance,GROUP_CONCAT(total) total,SUM(risk) risk from (
select name,entrance,count(risk) total,sum(risk) risk from (
select sa.name,
CASE WHEN bet.entrance = 1 THEN '进口' ELSE '国产' END entrance,
bme.risk from bs_environment bet
left join bs_manage bme on bme.kid = bet.market_name
left join sys_area sa on bet.city_id = sa.kid


         AND sampling_date >= #{sampling_date_start} 


         AND sampling_date <= #{sampling_date_end} 


) lt group by name,entrance
) gt group by name) tt group by name,total order by name DESC
